class KelasBaru:
    def metode_saya(self):
        print('Halo dari KelasBaru')

class KelasBaru:
    def __init__(self, nilai):
        self.nilai = nilai
objek = KelasBaru(10)
print(objek.nilai)

class Induk:
    def metode_induk(self):
        print('Halo dari Induk')
class Anak(Induk):
    def metode_anak(self):
        print('Halo dari Anak')
objek = Anak()
objek.metode_induk()

class KelasBaru:
    def __init__(self):
        self.__atribut_privat = 'privat'
    def get_atribut_privat(self):
        return self.__atribut_privat
objek = KelasBaru()
print(objek.get_atribut_privat())

class KelasBaru:
    @staticmethod
    def metode_statis():
        print('Ini adalah metode statis')
KelasBaru.metode_statis()

class Hewan:
    def suara(self):
        raise NotImplementedError
class Anjing(Hewan):
    def suara(self):
        return 'Guk'
class Kucing(Hewan):
    def suara(self):
        return 'Meong'
hewan = [Anjing(), Kucing()]
for h in hewan:
    print(h.suara())
