def faktorial(n):
    if n == 0:
        return 1
    else:
        return n * faktorial(n-1)

print(faktorial(5))

def fibonacci(n):
    if n <= 1:
        return n
    else:
        return fibonacci(n-1) + fibonacci(n-2)

print(fibonacci(6))

def balik_kata(kata):
    if len(kata) == 0:
        return kata
    else:
        return balik_kata(kata[1:]) + kata[0]

print(balik_kata('Python'))

