my_set = {1, 2, 3, 4, 5}
print('Set:', my_set)
print('Tipe data:', type(my_set))

my_set = {1, 2, 3}
my_set.add(4)
print('Set setelah penambahan:', my_set)

my_set = {1, 2, 3, 4}
my_set.remove(3)
print('Set setelah penghapusan:', my_set)

set_a = {1, 2, 3}
set_b = {3, 4, 5}
union_set = set_a | set_b
print('Union dari dua set:', union_set)

set_a = {1, 2, 3}
set_b = {3, 4, 5}
intersection_set = set_a & set_b
print('Intersection dari dua set:', intersection_set)

set_a = {1, 2, 3}
set_b = {3, 4, 5}
difference_set = set_a - set_b
print('Difference dari dua set:', difference_set)

set_a = {1, 2, 3}
set_b = {3, 4, 5}
symmetric_difference_set = set_a ^ set_b
print('Symmetric difference dari dua set:', symmetric_difference_set)
