def cetak_ganjil(n):
    """
    Fungsi untuk mencetak angka ganjil dari 1 hingga n
    """
    for i in range(1, n+1):
        if i % 2 != 0:
            print(i)

# Memanggil fungsi cetak_ganjil dengan argumen 10
cetak_ganjil(10)

