for i in range(1, 11):
    # Mengecek apakah angka adalah bilangan prima
    prima = True
    if i > 1:
        for j in range(2, i):
            if i % j == 0:
                prima = False
                break
    else:
        prima = False
    if prima:
        print(f'{i} adalah bilangan prima')

def cek_bilangan_prima(angka):
    """
    Fungsi untuk memeriksa apakah sebuah angka adalah bilangan prima.
    Bilangan prima adalah bilangan yang hanya dapat dibagi oleh 1 dan dirinya sendiri.
    """
    if angka < 2:
        return False
    for i in range(2, int(angka ** 0.5) + 1):
        if angka % i == 0:
            return False
    return True

print(cek_bilangan_prima(17))

