# Example of building a RESTful API with Flask
from flask import Flask, jsonify, request

app = Flask(__name__)

# Define a route and function for GET request
@app.route('/api', methods=['GET'])
def get_data():
    data = {'message': 'Hello, world!'}
    return jsonify(data)

if __name__ == '__main__':
    app.run(debug=True)
