   void BinaryTree::insert(int value) {
       Node* newNode = new Node(value);
       if (root == nullptr) {
           root = newNode;
           return;
       }

       Node* current = root;
       Node* parent;
       while (true) {
           parent = current;
           if (value < current->data) {
               current = current->left;
               if (current == nullptr) {
                   parent->left = newNode;
                   return;
               }
           } else {
               current = current->right;
               if (current == nullptr) {
                   parent->right = newNode;
                   return;
               }
           }
       }
   }
   ```

